import React, { useState, useEffect } from "react";
import { Card, Row, Col } from "antd";
import axios from "axios";

const ViewItem = ({ items }) => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [itemData, setItemData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Simulate loading delay (remove this in actual code)
        await new Promise((resolve) => setTimeout(resolve, 1000));

        // Fetch data using axios (replace this with your actual API call)
        // const response = await axios.get("your-api-endpoint");
        // const itemsData = response.data;

        // Example data (remove this in actual code)
        const itemsData = [
          {
            id: 1,
            name: "Item 1",
            image:
              "data:image/jpeg;base64,",
            price: 10,
          },
          {
            id: 2,
            name: "Item 2",
            image:
              "data:image/jpeg;base64,",
            price: 20,
          },
          {
            id: 2,
            name: "Item 2",
            image:
              "data:image/jpeg;base64,",
            price: 20,
          },
          // Add more items as needed
        ];

        // Update state with fetched data
        setItemData(itemsData);
        setLoading(false);
      } catch (error) {
        // Handle errors
        setLoading(false);
        setError("Error fetching data.");
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  if (!itemData || itemData.length === 0) {
    return <p>No item data available.</p>;
  }

  return (
    <div style={{ padding: "24px" }}>
      <Row gutter={[16, 16]}>
        {itemData.map((item) => (
          <Col key={item.id} xs={24} sm={16} md={12} lg={8} xl={6}>
            <Card
              title={item.name}
              cover={<img alt={item.name} src={item.image} />}
            >
              <p>Price: ${item.price}</p>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default ViewItem;
